import type { GlobalTheme } from '../global';

const getFileTheme = (theme: GlobalTheme) => {
  const sizes = {
    dragAndDropMinHeight: '144px',
  };

  const space = {
    dragAndDropPadding: `${theme.space.xlarge}px ${theme.space.xlarge}px`,
  };

  const radii = {
    dragAndDrop: `${theme.radii.base}px`,
  };

  const colors = {
    dragAndDropBackground: theme.colors.defaultBackground,
    dragAndDropBorder: theme.colors.defaultBorder,
    dragAndDropBorderHover: theme.colors.primary,
  };

  return { sizes, space, radii, colors };
};

type FileTheme = ReturnType<typeof getFileTheme>;

export { getFileTheme };
export type { FileTheme };
