import type { GlobalTheme } from '../global';

const getFormTheme = (theme: GlobalTheme) => {
  const space = {
    smallIconPadding: `${theme.space.xsmall}px`,
    mediumIconPadding: `${theme.space.small}px`,
    largeIconPadding: `${theme.space.small}px`,
  };

  const colors = {
    errorText: theme.colors.danger,
    errorBorder: theme.colors.danger,
    warningBorder: theme.colors.warning,
  };

  const shadows = {
    focus: theme.shadows.primary,
    errorFocus: theme.shadows.danger,
    warningFocus: theme.shadows.warning,
  };

  return { space, colors, shadows };
};

type FormTheme = ReturnType<typeof getFormTheme>;

export { getFormTheme };
export type { FormTheme };
