import type { GlobalTheme } from '../global';

const getIconTheme = (theme: GlobalTheme) => {
  const fontSizes = {
    inherit: 'inherit',
    xsmall: `${theme.fontSizes.small}px`,
    small: `${theme.fontSizes.medium}px`,
    medium: `${theme.fontSizes.large}px`,
    large: `${theme.fontSizes.xlarge}px`,
    xlarge: `${theme.fontSizes.xxlarge}px`,
  };

  const sizes = fontSizes;

  const colors = {
    info: theme.colors.info,
    inherit: 'inherit',
    text: theme.colors.text,
    subduedText: theme.colors.subduedText,
    disabledText: theme.colors.disabledText,
    primary: theme.colors.primary,
    danger: theme.colors.danger,
    warning: theme.colors.warning,
    success: theme.colors.success,
    error: theme.colors.error,
  };

  return { fontSizes, sizes, colors };
};

type IconTheme = ReturnType<typeof getIconTheme>;

export { getIconTheme };
export type { IconTheme };
