import type { GlobalTheme } from '../global';

const getInPageNavigationTheme = (theme: GlobalTheme) => {
  const colors = {
    itemText: theme.colors.text,
    itemHoverBackgound: `${theme.colors.primary}0d`,
    itemClickedBackground: theme.colors.primaryLightBackground,
  };

  const space = {
    itemPadding: `${theme.space.small}px`,
    itemMarginBottom: `${theme.space.xsmall}px`,
    itemMarginRight: `${theme.space.xsmall}px`,
    itemGap: `${theme.space.small}px`,
  };

  const radii = {
    item: `${theme.radii.base}px`,
  };

  return { colors, space, radii };
};

type InPageNavigationTheme = ReturnType<typeof getInPageNavigationTheme>;

export { getInPageNavigationTheme };
export type { InPageNavigationTheme };
