import type { GlobalTheme } from '../../global';
import type { BannerTheme } from '../banner';
import { getBannerTheme as getBannerHeroTheme } from '../banner';

const getBannerTheme = (theme: GlobalTheme): BannerTheme => {
  const bannerHeroTheme = getBannerHeroTheme(theme);

  return {
    ...bannerHeroTheme,
    colors: {
      ...bannerHeroTheme.colors,
      info: theme.colors.lightText,
      textInfo: theme.colors.lightText,
    },
  };
};

export { getBannerTheme };
