import type { GlobalTheme } from '../global';

const getLegendTheme = (theme: GlobalTheme) => {
  const radii = {
    legendCircle: '50%',
  };

  const sizes = {
    legendWidth: `${theme.space.small}px`,
    legendHeight: `${theme.space.small}px`,
  };

  const space = {
    legendCircleMarginRight: `${theme.space.small}px`,
  };

  return {
    radii,
    space,
    sizes,
  };
};

type LegendTheme = ReturnType<typeof getLegendTheme>;

export { getLegendTheme };
export type { LegendTheme };
