import type { DeepPartial } from '../../../fp/Object';
import type { GlobalTheme } from '../../global';
import { getEmptyTheme as getEmptyHeroTheme } from '../empty';

type EmptyHeroTheme = ReturnType<typeof getEmptyHeroTheme>;
type OmopColors = DeepPartial<EmptyHeroTheme['colors']>;

const getEmptyTheme = (theme: GlobalTheme): EmptyHeroTheme => {
  const heroTheme = getEmptyHeroTheme(theme);

  const omopColors: OmopColors = {
    text: theme.colors.subduedText,
  };

  return {
    ...heroTheme,
    colors: {
      ...heroTheme.colors,
      ...omopColors,
    },
  };
};

export { getEmptyTheme };
