import type { GlobalTheme } from '../global';

const getPageHeaderTheme = (theme: GlobalTheme) => {
  const space = {
    titleMargin: `${theme.space.large}px 0`,
  };
  const colors = {
    title: theme.colors.text,
  };

  const fontSizes = {
    title: `${theme.fontSizes.xxxxxlarge}px`,
  };

  const fontWeights = {
    title: theme.fontWeights.semiBold,
  };

  return { space, colors, fontSizes, fontWeights };
};

type PageHeaderTheme = ReturnType<typeof getPageHeaderTheme>;

export { getPageHeaderTheme };
export type { PageHeaderTheme };
