import type { GlobalTheme } from '../global';

const getPortletTheme = (theme: GlobalTheme) => {
  const colors = {
    wrapperBackground: theme.colors.defaultLightBackground,
  };
  const space = {
    wrapperPadding: `${theme.space.large}px`,
    headerWrapperMargin: `${theme.space.medium}px`,
    headerTitleMargin: `${theme.space.small}px`,
    actionWrapperMargin: `${theme.space.medium}px`,
    actionButtonMargin: `${theme.space.small}px`,
  };
  const radii = {
    wrapper: `${theme.radii.large}px`,
  };
  const borderWidths = {
    wrapper: `${theme.borderWidths.base}px`,
  };

  return { colors, space, radii, borderWidths };
};

type PortletTheme = ReturnType<typeof getPortletTheme>;

export { getPortletTheme };
export type { PortletTheme };
