import type { GlobalTheme } from '../global';

const getRateTheme = (theme: GlobalTheme) => {
  const colors = {
    inactiveIcon: theme.colors.palette.grey,
    activeIcon: theme.colors.palette.yellowDark15,
    disabledIcon: theme.colors.disabledText,
  };

  const space = {
    rateGroupMediumMargin: `-${theme.space.xsmall}px 0 0 -${theme.space.xsmall}px`,
    rateGroupLargeMargin: `-${theme.space.small}px 0 0 -${theme.space.small}px`,
    rateMediumMargin: `${theme.space.xsmall}px 0 0 ${theme.space.xsmall}px`,
    rateLargeMargin: `${theme.space.small}px 0 0 ${theme.space.small}px`,
  };

  return {
    colors,
    space,
  };
};

type RateTheme = ReturnType<typeof getRateTheme>;

export { getRateTheme };
export type { RateTheme };
