import type { GlobalTheme } from '../global';

const getResultTheme = (theme: GlobalTheme) => {
  const colors = {
    default: theme.colors.text,
  };

  const space = {
    titleMargin: `${theme.space.large}px 0`,
    extraMargin: `${theme.space.xlarge}px`,
  };

  const fontWeights = {
    title: theme.fontWeights.semiBold,
    subTitle: theme.fontWeights.regular,
  };

  const fontSizes = {
    icon: `${theme.fontSizes.xxxxxlarge * 2}px`,
    title: `${theme.fontSizes.xxxlarge}px`,
    subTitle: `${theme.fontSizes.medium}px`,
  };

  const lineHeights = {
    title: `${theme.lineHeights.xxxlarge}px`,
    subTitle: `${theme.lineHeights.medium}px`,
  };

  return { colors, space, fontWeights, fontSizes, lineHeights };
};

type ResultTheme = ReturnType<typeof getResultTheme>;

export { getResultTheme };
export type { ResultTheme };
