import type { GlobalTheme } from '../global';

const getSelectTheme = (theme: GlobalTheme) => {
  const sizes = {
    menuMaxHeight: '250px',
    itemsPerPageInputWidth: '65px',
  };

  const space = {
    menuCategoryPadding: `${theme.space.xsmall}px ${theme.space.small}px`,
    menuCheckmarkMarginLeft: `${theme.space.small}px`,
    loadingIconPaddingRight: `${theme.space.xsmall}px`,
  };

  const fontSizes = {
    menuCategory: `${theme.fontSizes.small}px`,
  };

  const lineHeights = {
    menuCategory: `${theme.lineHeights.small}px`,
  };

  return { sizes, space, fontSizes, lineHeights };
};

type SelectTheme = ReturnType<typeof getSelectTheme>;

export { getSelectTheme };
export type { SelectTheme };
