import type { GlobalTheme } from '../global';

const getStatisticTheme = (theme: GlobalTheme) => {
  const colors = {
    titleWhite: theme.colors.lightText,
    titleText: theme.colors.subduedText,
    contentWhite: theme.colors.lightText,
    contentText: theme.colors.text,
  };

  const fontWeights = {
    content: theme.fontWeights.semiBold,
  };

  const fontSizes = {
    title: `${theme.fontSizes.medium}px`,
    content: `${theme.fontSizes.xxxlarge}px`,
  };

  const lineHeights = {
    default: `${theme.lineHeights.medium}px`,
  };

  const space = {
    titleWrapperMargin: `0 0 ${theme.space.small}px 0`,
    prefixWrapperMargin: `0 ${theme.space.xsmall}px 0 0`,
    suffixWrapperMargin: `0 0 0 ${theme.space.xsmall}px`,
  };

  return { colors, fontWeights, fontSizes, lineHeights, space };
};

type StatisticTheme = ReturnType<typeof getStatisticTheme>;

export { getStatisticTheme };
export type { StatisticTheme };
