import { defaultScale, jobsSystemPalette, getGlobalTheme } from './global';
import getTheme from './getTheme';
import { getBannerTheme } from './components/jobs/banner';

import type { Theme } from './types';

const getJobsTheme = (
  scale = defaultScale,
  systemPalette = jobsSystemPalette
): Theme => {
  const globalTheme = getGlobalTheme(scale, systemPalette);
  const defaultTheme = getTheme(scale, systemPalette);

  return {
    ...defaultTheme,
    __hd__: {
      ...defaultTheme.__hd__,
      banner: getBannerTheme(globalTheme),
    },
  };
};

export default getJobsTheme;
