interface BorderWidths {
  base: number;
}

interface Radii {
  base: number;
  medium: number;
  large: number;
}

const getBorderWidths = (baseBorderWidth: number): BorderWidths => ({
  base: baseBorderWidth,
});

const getRadii = (baseRadius: number): Radii => ({
  base: baseRadius,
  medium: baseRadius * 2.5,
  large: baseRadius * 5,
});

export { getBorderWidths, getRadii };
export type { BorderWidths, Radii };
