const getBreakpoints = ({
  small,
  medium,
  large,
  xlarge,
}: {
  small: number;
  medium: number;
  large: number;
  xlarge: number;
}) => ({
  small: `${small}px`,
  medium: `${medium}px`,
  large: `${large}px`,
  xlarge: `${xlarge}px`,
});

type Breakpoints = ReturnType<typeof getBreakpoints>;

export { getBreakpoints };
export type { Breakpoints };
