import type { SystemPalette } from './colors';

const getShadows = (systemPalette: SystemPalette) => ({
  default: `0 2px 7px 1px ${systemPalette.defaultDarkBackground}25`,
  primary: `0 0 0 2px ${systemPalette.primaryLightBackground}`,
  danger: `0 0 0 2px ${systemPalette.dangerBackground}`,
  warning: `0 0 0 2px ${systemPalette.warningBackground}`,
  divider: `inset 10px 0 8px -8px ${systemPalette.defaultDarkGreyBackground}`,
});

type Shadows = ReturnType<typeof getShadows>;

export { getShadows };
export type { Shadows };
