interface Sizes {
  large: number;
  medium: number;
  small: number;
  xlarge: number;
  xsmall: number;
  xxlarge: number;
  xxsmall: number;
  xxxlarge: number;
}

const getSizes = (baseSize: number): Sizes => ({
  xxsmall: baseSize * 0.25,
  xsmall: baseSize * 0.5,
  small: baseSize,
  medium: baseSize * 2,
  large: baseSize * 3,
  xlarge: baseSize * 4,
  xxlarge: baseSize * 5,
  xxxlarge: baseSize * 6,
});

export { getSizes };
export type { Sizes };
