interface Space {
  large: number;
  medium: number;
  small: number;
  xlarge: number;
  xsmall: number;
  xxlarge: number;
  xxsmall: number;
  xxxlarge: number;
  xxxxlarge: number;
}

const getSpace = (baseSpace: number): Space => ({
  xxsmall: baseSpace * 0.25,
  xsmall: baseSpace * 0.5,
  small: baseSpace,
  medium: baseSpace * 2,
  large: baseSpace * 3,
  xlarge: baseSpace * 4,
  xxlarge: baseSpace * 5,
  xxxlarge: baseSpace * 6,
  xxxxlarge: baseSpace * 7,
});

export { getSpace };
export type { Space };
