import { GlobalTheme } from '../theme/global';

const breakpoints = ['small', 'medium', 'large', 'xlarge'] as const;
type Breakpoint = typeof breakpoints[number];

const getBreakpointValue = (bp: Breakpoint, theme: GlobalTheme) => {
  switch (bp) {
    case 'small':
      return theme.breakpoints.small;
    case 'medium':
      return theme.breakpoints.medium;
    case 'large':
      return theme.breakpoints.large;
    case 'xlarge':
      return theme.breakpoints.xlarge;
  }
};

const getMediaQuery = (bp: Breakpoint, theme: GlobalTheme) =>
  `(min-width: ${getBreakpointValue(bp, theme)})`;

export { breakpoints, getBreakpointValue, getMediaQuery };
export type { Breakpoint };
