declare type BaseOption<V extends string | number> = {
    disabled?: boolean;
    helpText?: string;
    text: string;
    value: V;
};
declare type GroupedOption<V extends string | number, T extends BaseOption<V>> = {
    category: string;
    options: T[];
};
declare type MultiBaseOption<V extends string | number> = BaseOption<V> & {
    removable?: boolean;
};
declare type GroupedMultiOption<V extends string | number, T extends MultiBaseOption<V>> = {
    category: string;
    options: T[];
};
export type { BaseOption, GroupedOption, MultiBaseOption, GroupedMultiOption };
